import { useState, useEffect, useRef } from 'react'

function Counter(props) {
    const [count, setCount] = useState(0)
    const [input, setInput] = useState('')
    const [initialMount, setInitalMount] = useState(true)

    const isInitalMount = useRef(true)

    useEffect(() => {
        // document.title = `Count - ${count}`

        // const initialCount = Number(localStorage.getItem('count'))

        // setCount(initialCount)


        // if (isInitalMount) {
        //     // do something on first load

        //     isInitalMount.current = false
        // } else {
        //     // do something on updates
        // }

        if(initialMount) {
            // do something on first load
            console.log('inital mount')
            setInitalMount(false)
        } else {
            // do something on update
            console.log('update')
        }
    }, [initialMount])

    const handleIncrement = () => {
        const newCount = count + 1

        setCount(newCount)

        localStorage.setItem('count', newCount)
    }

    const handleDecrement = () => {
        const newCount = count - 1

        setCount(newCount)

        localStorage.setItem('count', newCount)
    }

    const handleSet = () => {
        setCount(input)

        localStorage.setItem("count", input)
    }

    return (
        <div>
            <h2>Count: {count}</h2>
            <button onClick={handleIncrement}>Increment</button>
            <button onClick={handleDecrement}>Decrement</button>
            <br />
            <br />
            <div>
                <input type="number" value={input} onChange={(event) => setInput(event.target.value)} />
                <button onClick={handleSet}>set</button>
            </div>
        </div>
    )
}

export default Counter
