function ToDo({ index, task, handleToggle, handleRemove }) {
    return (
        <li key={index}>
            <span
                onClick={() => handleToggle(index)}
                style={{ textDecoration: task.completed && 'line-through' }}>
                {task.taskName}
            </span>
            <button onClick={() => console.log(index)}>remove</button>
        </li>
    )
}

