import { Component } from 'react'

class ToDoList extends Component {

    state = {
        toDos: [],
        input: ''
    }

    handleSubmit = (event) => {
        event.preventDefault()

        this.setState({ toDos: [...this.state.toDos, { taskName: this.state.input, completed: false}], input: '' })
    }

    handleChange = (event) => {
        this.setState({ input: event.target.value })
    }

    handleToggle = (event, index) => {
        const newArray = [...this.state.toDos]

        newArray[index].completed = !newArray[index].completed

        this.setState({ toDos: newArray })
    }

    handleDelete = (event) => {
        event.stopPropagation()
        console.log('delete!')
    }

    render() {
        return (
            <div>
                <h2>To Do list</h2>

                <form onSubmit={this.handleSubmit}>
                    <input value={this.state.input} onChange={this.handleChange} />
                    <button>add</button>
                </form>

                <ul>
                    {this.state.toDos.map((toDo, index) => {
                        return (
                            <li 
                                key={index} 
                                onClick={(event) => this.handleToggle(event, index)}
                                style={{textDecoration: toDo.completed && 'line-through'}}
                            >
                                {toDo.taskName}
                                <button onClick={this.handleDelete}>delete</button>
                            </li>
                        )
                    })}
                </ul>
            </div>
        )
    }
}

export default ToDoList
