import React from 'react'

class Counter extends React.Component {

    state = {
        count: 0,
        someArray: [],
        someNestedArray: [1, 2, [2, 2, 3]],
        someObject: {
            foo: 'bar'
        }
    }

    handleIncrement = () => {
        console.log(this)
       this.setState({ count: this.state.count + 1, someNestedArray: 'foo' })
    }

    render() {
        return (
            <div>
                {/* <h1>Counter: {this.state.count}</h1> */}
                <button onClick={this.handleIncrement}>+1</button>
            </div>
        )
    }
}

export default Counter
